mapper.select:
SELECT * FROM PRODUCT
WHERE COM_NUM = #{comNum}
AND SELL_TYPE = 'Y'

SELECT *
FROM (
    SELECT * FROM PRODUCT
    WHERE COM_NUM = #{comNum}
      AND PRD_NUM != #{productId}
      AND SELL_TYPE = 'Y'
    ORDER BY DBMS_RANDOM.VALUE
)

    SELECT COUNT(*) AS product_count
	FROM PRODUCT
	WHERE COM_NUM = #{comNum}

SELECT * FROM PRODUCT 
WHERE COM_NUM = #{comNum} 
AND SELL_TYPE = #{sellType}
