mapper.update:
UPDATE PRODUCT
SET CLICK_NUM = CLICK_NUM + 1
WHERE PRD_NUM = #{prdNum}

mapper.select:
SELECT COUNT(*) 
FROM CHATROOM 
WHERE PRD_NUM = #{prdNum}

SELECT nickname, manner
FROM users
WHERE user_num = #{userNum}

SELECT *
FROM (
  SELECT *
  FROM PRODUCT
  WHERE USER_NUM = #{userNum}
    AND PRD_NUM != #{excludePrdNum}
  ORDER BY DBMS_RANDOM.VALUE
)
WHERE ROWNUM = 1

SELECT *
FROM (
  SELECT *
  FROM PRODUCT
  WHERE CAT_NUM = #{catNum}
    AND PRD_NUM != #{prdNum}
    ORDER BY DBMS_RANDOM.VALUE
)
WHERE ROWNUM <= 15
